import React, {useEffect, useState} from "react";
import { db } from "../firebase";
import { Link } from "react-router-dom"; 
import "./Categories.css"
import Navbar from "../Navbar";
import { useParams } from "react-router-dom";


const Categories = () => {
  const [categories, setCategories] = useState([])
  const {id} = useParams()

  useEffect(() => {
    window.scrollTo(0, 0);
    let data = []
    db.collection("Schools").doc(id).collection("Categories").get().then((snapshot) => (
      snapshot.forEach((doc) => (
        data.push({
          id: doc.id,
          name: doc.data().name,
        })
      ))
    ))
    setCategories(data)
  }, [])

  console.log(categories);
    return(

      <div>
        <Navbar />
        <div className="categories">

         <h1 className="listTitle">
         Choose course category from {id}

        </h1>
        <div className="categoryComponent">

          {categories.toArray().map((category) => (
            <Link id="link" to={`/schools/${id}/categories/${category.id}`}>
            <div className="categoryBox" key={category.id}>


                    <div>
                        <h1 >{category.name}</h1>




                          </div>
                        </div>
                          </Link>
          ))}
        </div>
      </div>
      </div>

      );
    }







export default Categories;
