import { React, useEffect, useState } from "react";
import {
  BrowserRouter as Router,
  Link,
  Route,
  Routes,
  useParams,
} from "react-router-dom";
import "./Courses.css";
import Navbar from "../Navbar";
import { getCourses } from "../Backend/firebase-functions";

function Courses() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = React.useState(true);

  const { id, catId } = useParams();
  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const data = await getCourses(id, catId);
      setCourses(data);
      setLoading(false);
    } catch (error) {
      console.log(error);
    }
  };

  const content = courses.map((post) => (
    <Link id="link" to={`/categories/${id}/${post.id}`}>
      <div className="courseBox" key={post.name}>
        <h3>{post.fullName}</h3>
      </div>
    </Link>
  ));

  return (
    <div>
      <Navbar />
      <div className="courses">
        <h1 className="listTitle">
          <strong>{id} </strong> courses at{" "}
          <strong>Stevens Institute of Technology</strong>
        </h1>
        <div className="courseComponent">{content}</div>
      </div>
    </div>
  );
}

export default Courses;
