import React, {useEffect, useState} from "react";
import { db } from "../firebase";
import { Link } from "react-router-dom"; 
import "./Categories.css"
import Navbar from "../Navbar";


const Categories = () => {
  const [categories, setCategories] = useState([])


  //useeffect function that grabs the categories from Stevens in Schools collection and adds them to the categories props

  useEffect(() => {
    window.scrollTo(0, 0);
    let data = []
    db.collection("Schools").doc("Stevens").collection("Categories").get().then((snapshot) => (
      snapshot.forEach((doc) => (
        data.push({
          id: doc.id,
          name: doc.data().name,
          catId: doc.data().catId
        })
      ))
    ))
    setCategories(data)
  }, [])


    return(
      <div>
        <Navbar />
        <div className="categories">

         <h1 className="listTitle">
         Choose course category from

        </h1>
        <div className="categoryComponent">
       { categories.map((p) => (
      <Link id="link" to={`/schools/Stevens/categories/${p.catId}`}>
      <div className="categoryBox" key={p.id}>

              <div>
                  <h1 >{p.name}</h1>
              </div>


      </div>  </Link>))
  }
        </div>
      </div>
      </div>

      );
    }







export default Categories;
