import React, {useEffect, useState} from "react";
import { db } from "../firebase";
import { Link } from "react-router-dom"; 
import "./Categories.css"
import Navbar from "../Navbar";


const Categories = () => {
  const [projects, setProjects] = useState([])


  //useeffect function that grabs the categories from firebase
  useEffect(() => {
    window.scrollTo(0, 0);
    db.collection("Schools", "Stevens", "Categories").get().then((snapshot) => (

        snapshot.forEach((doc) => (
            setProjects((prevState) => ({
                projects: [...prevState.projects,
                  {
                      id: doc.id, 
                      name: doc.data().name,

                }]
            }))
        ))
    ))
  }, []);


    return(
      <div>
        <Navbar />
        <div className="categories">

         <h1 className="listTitle">
         Choose course category from

        </h1>
        <div className="categoryComponent">
       { projects.map((p) => (
      <Link id="link" to={`/schools/Stevens/categories/${p.catId}`}>
      <div className="categoryBox" key={p.id}>

              <div>
                  <h1 >{p.name}</h1>
              </div>


      </div>  </Link>))
  }
        </div>
      </div>
      </div>

      );
    }







export default Categories;
