import { db } from "./firebase";

// get all categories in the school subcollection
export const getCategories = async (school) => {
  const categories = await db
    .collection("Schools")
    .doc(school)
    .collection("Categories")
    .get();
  return categories.docs.map((doc) => doc.data());
};
